from datetime import datetime
import xml.etree.cElementTree as ET
import spotipy
import random
from spotipy.oauth2 import SpotifyClientCredentials

now = datetime.now() # current date and time
date_time = now.strftime('%d %m %Y')

temp = ''

### Spotify Section
   # Gathering playlist data using Spotipy library and Spotify Developer API credentials
#Authentication without user credentials
client_credentials_manager = SpotifyClientCredentials(
    client_id = '821efc5f725c486696a6bfc899f85cd7', 
    client_secret = 'be024c23c4c74181af68b7805b92cf6c')
sp = spotipy.Spotify(client_credentials_manager = client_credentials_manager)
playlist_link = 'https://open.spotify.com/playlist/78YmIyAEzfXP2wPy88Emc6?si=aff2ee9c9125464f'
playlist_id = playlist_link.split('/')[-1].split('?')[0]
playlist_tracks = sp.playlist_tracks(playlist_id)['items']

# Uncomment to see complete playlist contents
#for i, track in sp.playlist_tracks(playlist_id)['items']:
