# Initial XML structure
print('Setting up XML file structure...')
round = ET.Element('round')
ET.SubElement(round, 'game').text = 'Quizsentials'
ET.SubElement(round, 'title').text = f'SQ Music Round {date_time}'
ET.SubElement(round, 'points_per_question').text = '10'
ET.SubElement(round, 'go_wide').text = 'true'
ET.SubElement(round, 'speed_bonus').text = 'true'

questions = ET.SubElement(round, 'questions')    

# Generate question XML structure for every item in playlist
for i, track in enumerate(playlist_tracks, start=1):
    track_name = track['track']['name']
    artist_name = track['track']['artists'][0]['name']
    song_number = f"{i}"
    q_text = ''

    question_target = random.choice(['ARTIST', 'SONG'])

    #print(artist_name.upper().split()[1:])
    print(artist_name[0].isdigit())
    # Check if the question target is 'ARTIST'
    if question_target == 'ARTIST':
        # Handle exception of both inputs being invalid
        if artist_name.upper().split()[0] in ['THE', 'A', 'AN'] or artist_name[0].isdigit():
            if track_name.upper().split()[0] in ['THE', 'A', 'AN'] or track_name[0].isdigit():
                raise ValueError(f'SONG #{i} INVALID: Both artist_name ({artist_name}, first char {artist_name[0]}) and track_name ({track_name}, first char {track_name[0]}) start with articles or numbers. Target was {question_target}, ({artist_name} with "{track_name})". Please swap out this entry for a valid one.')
            else:
                q_text = f'MUSIC ROUND #{song_number} - Tap on the first letter of the SONG TITLE'
                s_answer = track_name[0].upper()
                l_answer = f'"{track_name}" by {artist_name}'
                print(f'XML Question element #{i} successfully added: answer set to "{s_answer}" as target is SONG for entry "{track_name}" by {artist_name}.')
        else:
            q_text = f'MUSIC ROUND #{song_number} - Tap on the first letter of the ARTIST name'
            s_answer = artist_name[0].upper()
            l_answer = f'{artist_name} with "{track_name}"'
            print(f'XML Question element #{i} successfully added: answer set to "{s_answer}" as target is ARTIST for entry {artist_name} with"{track_name}" by ')

    # Check if the question target is 'SONG'
    if question_target == 'SONG':
        # Handle exception of both inputs being invalid
        if track_name.upper().split()[0] in ['THE', 'A', 'AN'] or track_name[0].isdigit():
            if artist_name.upper().split()[0] in ['THE', 'A', 'AN'] or 	artist_name[0].isdigit():
                raise ValueError(f'SONG #{i} INVALID: Both artist_name ({artist_name}, first char {artist_name[0]}) and track_name ({track_name}, first char {track_name[0]}) start with articles or numbers. Target was {question_target}, ({artist_name} with "{track_name})". Please swap out this entry for a valid one.')
            else:
                q_text = f'MUSIC ROUND #{song_number} - Tap on the first letter of the ARTIST name'
                s_answer = artist_name[0].upper()
                l_answer = f'{artist_name} with "{track_name}"'
                print(f'XML Question element #{i} successfully added: answer set to "{s_answer}" as target is ARTIST for entry {artist_name} with"{track_name}" by ')
        else:
            q_text = f'MUSIC ROUND #{song_number} - Tap on the first letter of the SONG TITLE'
            s_answer = track_name[0].upper()
            l_answer = f'"{track_name}" by {artist_name}'
            print(f'XML Question element #{i} successfully added: answer set to "{s_answer}" as target is SONG for entry "{track_name}" by {artist_name}.')


    question = ET.SubElement(questions, 'question')
    ET.SubElement(question, 'user_view').text = 'letters'
    ET.SubElement(question, 'q').text = q_text
    ET.SubElement(question, 'short_answer').text = s_answer
    ET.SubElement(question, 'long_answer').text = l_answer
    ET.SubElement(question, 'picture').text = temp
    ET.SubElement(question, 'id').text = str(i)

tree = ET.ElementTree(round)
print('Generating XML file...')
tree.write(f'C:/Users/thijs/Desktop/SQ Music Round {date_time}.sqq', encoding='utf-8')
print('XML file generated!')
print('DONE: SpeedQuizzing music round successfully generated!')